import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function JogadoresAvulsos() {
  const navigate = useNavigate();
  const API =
    process.env.REACT_APP_API_URL || 'http://localhost:8000/api/v2';
  console.log('JogadoresAvulsos usando API:', API);

  const [lista, setLista] = useState([]);
  const [pessoas, setPessoas] = useState([]);
  const [form, setForm] = useState({ pessoa: '', posicao: '' });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('accessToken');
    if (!token) {
      console.warn('Faça login antes de carregar dados');
      setLoading(false);
      return;
    }
    Promise.all([
      fetch(`${API}/pessoas/`, {
        headers: { Authorization: `Bearer ${token}` },
      }).then(r => r.json()),
      fetch(`${API}/jogadores-avulsos/`, {
        headers: { Authorization: `Bearer ${token}` },
      }).then(r => r.json()),
    ])
      .then(([pList, jList]) => {
        setPessoas(pList);
        setLista(jList);
      })
      .catch(console.error)
      .finally(() => setLoading(false));
  }, [API]);

  const handleChange = e =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = e => {
    e.preventDefault();
    if (!form.pessoa) return alert('Selecione uma pessoa');
    const token = localStorage.getItem('accessToken');
    fetch(`${API}/jogadores-avulsos/`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ ...form, disponibilidade: true }),
    })
      .then(r => r.json())
      .then(novo => {
        setLista(prev => [...prev, novo]);
        setForm({ pessoa: form.pessoa, posicao: '' });
      })
      .catch(console.error);
  };

  const handleDelete = id => {
    const token = localStorage.getItem('accessToken');
    fetch(`${API}/jogadores-avulsos/${id}/`, {
      method: 'DELETE',
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(() => setLista(prev => prev.filter(j => j.id !== id)))
      .catch(console.error);
  };

  return (
    <div className="container mt-4">
      <button
        onClick={() => navigate(-1)}
        className="btn btn-secondary mb-3"
      >
        ← Voltar
      </button>
      <h2>Jogadores Avulsos</h2>

      <form
        onSubmit={handleSubmit}
        className="row g-2 mb-4 align-items-end"
      >
        <div className="col">
          <label className="form-label">Pessoa</label>
          <select
            name="pessoa"
            value={form.pessoa}
            onChange={handleChange}
            className="form-select"
            required
          >
            <option value="">— Selecione —</option>
            {pessoas.map(p => (
              <option key={p.id} value={p.id}>
                {p.email}
              </option>
            ))}
          </select>
        </div>
        <div className="col">
          <label className="form-label">Posição</label>
          <input
            name="posicao"
            placeholder="Ex: Atacante"
            value={form.posicao}
            onChange={handleChange}
            className="form-control"
            required
          />
        </div>
        <div className="col-auto">
          <button type="submit" className="btn btn-primary">
            Cadastrar
          </button>
        </div>
      </form>

      {loading ? (
        <p>Carregando...</p>
      ) : (
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Pessoa (email)</th>
              <th>Posição</th>
              <th>Disponível</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {lista.map(j => (
              <tr key={j.id}>
                <td>
                  {pessoas.find(p => p.id === j.pessoa)?.email ||
                    j.pessoa}
                </td>
                <td>{j.posicao}</td>
                <td>{j.disponibilidade ? '✔️' : '❌'}</td>
                <td>
                  <button
                    className="btn btn-danger btn-sm"
                    onClick={() => handleDelete(j.id)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
