import React, { useEffect, useState } from 'react';

export default function JogadoresAvulsos() {
  const [lista, setLista] = useState([]);
  const [form, setForm] = useState({ pessoa: '', posicao: '' });

  useEffect(() => {
    fetch('http://localhost:8000/api/v2/jogadores-avulsos/')
      .then(r => r.json())
      .then(setLista);
  }, []);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = e => {
    e.preventDefault();
    fetch('http://localhost:8000/api/v2/jogadores-avulsos/', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ...form, disponibilidade: true })
    })
    .then(r => r.json())
    .then(novo => setLista([...lista, novo]));
  };

  const handleDelete = id => {
    fetch(`http://localhost:8000/api/v2/jogadores-avulsos/${id}/`, { method: 'DELETE' })
      .then(() => setLista(lista.filter(j => j.id !== id)));
  };

  return (
    <div>
      <h2>Jogadores Avulsos</h2>
      <form onSubmit={handleSubmit} className="row g-2 mb-4 align-items-end">
        <div className="col">
          <input
            name="pessoa"
            placeholder="ID da Pessoa"
            value={form.pessoa}
            onChange={handleChange}
            className="form-control"
            required
          />
        </div>
        <div className="col">
          <input
            name="posicao"
            placeholder="Posição"
            value={form.posicao}
            onChange={handleChange}
            className="form-control"
            required
          />
        </div>
        <div className="col-auto">
          <button type="submit" className="btn btn-primary">Cadastrar</button>
        </div>
      </form>

      <table className="table table-striped">
        <thead>
          <tr><th>ID Pessoa</th><th>Posição</th><th>Disponível</th><th>Ações</th></tr>
        </thead>
        <tbody>
          {lista.map(j => (
            <tr key={j.id}>
              <td>{j.pessoa}</td>
              <td>{j.posicao}</td>
              <td>{j.disponibilidade ? '✔️' : '❌'}</td>
              <td>
                <button className="btn btn-danger btn-sm" onClick={() => handleDelete(j.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
);
}
