import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function JogadoresAvulsos() {
  const navigate = useNavigate();
  const API = process.env.REACT_APP_API_URL || 'http://localhost:8000/api/v2';

  const [lista, setLista] = useState([]);
  const [pessoas, setPessoas] = useState([]);
  const [form, setForm] = useState({
    pessoa: '',
    posicao: '',
    disponivelGeral: true,
    disponibilidades: []
  });
  const diasSemana = ['MONDAY','TUESDAY','WEDNESDAY','THURSDAY','FRIDAY','SATURDAY','SUNDAY'];

  useEffect(() => {
    const token = localStorage.getItem('accessToken');
    if (!token) return;
    Promise.all([
      fetch(`${API}/pessoas/`, { headers: { Authorization: `Bearer ${token}` } }).then(r => r.json()),
      fetch(`${API}/jogadores-avulsos/`, { headers: { Authorization: `Bearer ${token}` } }).then(r => r.json())
    ])
      .then(([ps, js]) => { setPessoas(ps); setLista(js); })
      .catch(console.error);
  }, [API]);

  const toggleDia = dia => {
    setForm(f => ({
      ...f,
      disponibilidades: f.disponibilidades.includes(dia)
        ? f.disponibilidades.filter(d => d !== dia)
        : [...f.disponibilidades, dia]
    }));
  };

  const handleChange = e => {
    const { name, value, type, checked } = e.target;
    setForm(f => ({
      ...f,
      [name]: type === 'checkbox' && name === 'disponivelGeral' ? checked : value
    }));
  };

  const handleSubmit = e => {
    e.preventDefault();
    const token = localStorage.getItem('accessToken');
    fetch(`${API}/jogadores-avulsos/`, {
      method: 'POST',
      headers: {
        'Content-Type':'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify(form)
    })
      .then(r => r.json())
      .then(novo => {
        setLista([...lista, novo]);
        setForm({ pessoa: form.pessoa, posicao:'', disponivelGeral:true, disponibilidades: [] });
      })
      .catch(console.error);
  };

  const handleDelete = id => {
    const token = localStorage.getItem('accessToken');
    fetch(`${API}/jogadores-avulsos/${id}/`, {
      method: 'DELETE',
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(() => setLista(lista.filter(j => j.id !== id)))
      .catch(console.error);
  };

  return (
    <div className="container mt-4">
      <button onClick={() => navigate(-1)} className="btn btn-secondary mb-3">← Voltar</button>
      <h2>Jogadores Avulsos</h2>

      <form onSubmit={handleSubmit} className="mb-4">
        <div className="row g-3 align-items-end">
          <div className="col-md-3">
            <label className="form-label">Pessoa</label>
            <select
              name="pessoa"
              value={form.pessoa}
              onChange={handleChange}
              className="form-select"
              required
            >
              <option value="">— Selecione —</option>
              {pessoas.map(p => <option key={p.id} value={p.id}>{p.email}</option>)}
            </select>
          </div>
          <div className="col-md-3">
            <label className="form-label">Posição</label>
            <input
              name="posicao"
              value={form.posicao}
              onChange={handleChange}
              className="form-control"
              placeholder="Ex: Atacante"
              required
            />
          </div>
          <div className="col-md-2">
            <div className="form-check">
              <input
                id="dispGeral"
                name="disponivelGeral"
                type="checkbox"
                checked={form.disponivelGeral}
                onChange={handleChange}
                className="form-check-input"
              />
              <label htmlFor="dispGeral" className="form-check-label">
                Disponível
              </label>
            </div>
          </div>
          <div className="col-md-4">
            <label className="form-label">Dias da Semana</label>
            <div className="d-flex flex-wrap">
              {diasSemana.map(d => (
                <div key={d} className="form-check me-3">
                  <input
                    id={`dia-j-${d}`}
                    type="checkbox"
                    className="form-check-input"
                    checked={form.disponibilidades.includes(d)}
                    onChange={() => toggleDia(d)}
                  />
                  <label htmlFor={`dia-j-${d}`} className="form-check-label">
                    {d.slice(0,3)}
                  </label>
                </div>
              ))}
            </div>
          </div>
        </div>
        <div className="text-end mt-3">
          <button className="btn btn-primary">Cadastrar</button>
        </div>
      </form>

      <table className="table table-striped">
        <thead>
          <tr>
            <th>Pessoa</th><th>Posição</th><th>Disp.</th><th>Dias</th><th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {lista.map(j => (
            <tr key={j.id}>
              <td>{pessoas.find(p=>p.id===j.pessoa)?.email}</td>
              <td>{j.posicao}</td>
              <td>{j.disponivelGeral ? '✔️':'❌'}</td>
              <td>{j.disponibilidades?.join(', ')}</td>
              <td>
                <button className="btn btn-danger btn-sm" onClick={()=>handleDelete(j.id)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
