// frontend/src/PessoasCrud.jsx
import React, { useEffect, useState } from 'react';

export default function PessoasCrud() {
  const API = process.env.REACT_APP_API_URL;  // ex: https://.../api/v2
  const [pessoas, setPessoas] = useState([]);
  const [email, setEmail] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('accessToken');
    fetch(`${API}/pessoas/`, {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    })
      .then(r => r.json())
      .then(setPessoas)
      .catch(console.error);
  }, [API]);

  const handleSubmit = e => {
    e.preventDefault();
    const token = localStorage.getItem('accessToken');
    fetch(`${API}/pessoas/`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify({ email })
    })
      .then(r => r.json())
      .then(nova => {
        setPessoas([...pessoas, nova]);
        setEmail('');
      })
      .catch(console.error);
  };

  return (
    <div className="container mt-4">
      <h2>Pessoas</h2>
      <form className="row g-2 mb-4 align-items-end" onSubmit={handleSubmit}>
        <div className="col">
          <label className="form-label">Email</label>
          <input
            type="email"
            className="form-control"
            placeholder="email@exemplo.com"
            value={email}
            onChange={e => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="col-auto">
          <button className="btn btn-primary">Cadastrar Pessoa</button>
        </div>
      </form>
      <ul className="list-group">
        {pessoas.map(p => (
          <li key={p.id} className="list-group-item">
            {p.id} â€” {p.email}
          </li>
        ))}
      </ul>
    </div>
  );
}
