// src/Login.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Login() {
  const [username, setUsername] = useState('');  // renomeado de email
  const [password, setPassword] = useState('');
  const [err, setErr] = useState('');
  const navigate = useNavigate();

  // REACT_APP_API_URL = https://…/api/v2
  // removemos o "/api/v2" para formar o base: https://… 
  const API_BASE = process.env.REACT_APP_API_URL.replace(/\/api\/v2$/, '');

  const handleSubmit = async e => {
    e.preventDefault();
    setErr('');
    try {
      const res = await fetch(`${API_BASE}/api/token/`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });

      const data = await res.json();
      if (!res.ok) {
        // se o JWT retornar um detail ou outro campo de erro, mostramos
        throw new Error(data.detail || 'Credenciais inválidas');
      }

      // armazenamos tokens e redirecionamos
      localStorage.setItem('accessToken', data.access);
      localStorage.setItem('refreshToken', data.refresh);
      navigate('/', { replace: true });
    } catch (error) {
      setErr(error.message);
    }
  };

  return (
    <div className="container mt-5" style={{ maxWidth: 400 }}>
      <h2 className="mb-4">Login</h2>
      {err && <div className="alert alert-danger">{err}</div>}

      <form onSubmit={handleSubmit}>
        {/* Usuário */}
        <div className="mb-3">
          <label className="form-label">Usuário</label>
          <input
            type="text"
            className="form-control"
            value={username}
            onChange={e => setUsername(e.target.value)}
            required
          />
        </div>

        {/* Senha */}
        <div className="mb-3">
          <label className="form-label">Senha</label>
          <input
            type="password"
            className="form-control"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
          />
        </div>

        <button className="btn btn-primary w-100">Entrar</button>
      </form>
    </div>
  );
}
