// src/SortearTimes.jsx
import React, { useEffect, useState } from 'react';

export default function SortearTimes() {
  const API = process.env.REACT_APP_API_URL;
  const token = localStorage.getItem('accessToken');

  const [pessoas, setPessoas] = useState([]);
  const [selectedIds, setSelectedIds] = useState([]);
  const [teams, setTeams] = useState({ team1: [], team2: [] });
  const [error, setError] = useState('');

  // 1) Carrega todas as pessoas
  useEffect(() => {
    fetch(`${API}/pessoas/`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(r => r.json())
      .then(setPessoas)
      .catch(console.error);
  }, [API, token]);

  // 2) Marcar/desmarcar pessoa
  const toggleSelect = id =>
    setSelectedIds(ids =>
      ids.includes(id) ? ids.filter(x => x !== id) : [...ids, id]
    );

  // 3) Bora sortear
  const handleSortear = () => {
    setError('');
    if (selectedIds.length < 2) {
      return setError('Selecione ao menos 2 pessoas para formar times.');
    }
    // filtra objetos Pessoa
    const pool = pessoas.filter(p => selectedIds.includes(p.id));
    // embaralha
    const embaralhado = pool
      .map(value => ({ value, sort: Math.random() }))
      .sort((a, b) => a.sort - b.sort)
      .map(({ value }) => value);
    // divide ao meio
    const meio = Math.ceil(embaralhado.length / 2);
    setTeams({
      team1: embaralhado.slice(0, meio),
      team2: embaralhado.slice(meio),
    });
  };

  return (
    <div className="container mt-4">
      <h2>Sorteio de Times</h2>
      <p>Selecione as pessoas e clique em Sortear</p>

      {error && <div className="alert alert-danger">{error}</div>}

      <div className="row">
        {pessoas.map(p => (
          <div key={p.id} className="col-md-4">
            <div className="form-check">
              <input
                id={`p-${p.id}`}
                type="checkbox"
                className="form-check-input"
                checked={selectedIds.includes(p.id)}
                onChange={() => toggleSelect(p.id)}
              />
              <label htmlFor={`p-${p.id}`} className="form-check-label">
                {p.email}
              </label>
            </div>
          </div>
        ))}
      </div>

      <div className="text-end mt-3">
        <button className="btn btn-primary" onClick={handleSortear}>
          Sortear
        </button>
      </div>

      <div className="row mt-4">
        <div className="col-md-6">
          <h5>Time A ({teams.team1.length})</h5>
          <ul className="list-group">
            {teams.team1.map(p => (
              <li key={p.id} className="list-group-item">
                {p.email}
              </li>
            ))}
          </ul>
        </div>
        <div className="col-md-6">
          <h5>Time B ({teams.team2.length})</h5>
          <ul className="list-group">
            {teams.team2.map(p => (
              <li key={p.id} className="list-group-item">
                {p.email}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
