// src/PessoasCrud.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function PessoasCrud() {
  const navigate = useNavigate();
  const API = process.env.REACT_APP_API_URL || 'http://localhost:8000/api/v2';

  const [pessoas, setPessoas] = useState([]);
  const [email, setEmail] = useState('');
  const [telefone, setTelefone] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('accessToken');
    if (!token) return setPessoas([]);
    fetch(`${API}/pessoas/`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(r => r.json())
      .then(setPessoas)
      .catch(console.error);
  }, [API]);

  const handleSubmit = e => {
    e.preventDefault();
    const token = localStorage.getItem('accessToken');
    fetch(`${API}/pessoas/`, {
      method: 'POST',
      headers: {
        'Content-Type':'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify({ email, telefone })
    })
      .then(r => r.json())
      .then(nova => {
        setPessoas(prev => [...prev, nova]);
        setEmail('');
        setTelefone('');
      })
      .catch(console.error);
  };

  return (
    <div className="container mt-4">
      <button onClick={() => navigate(-1)} className="btn btn-secondary mb-3">
        ← Voltar
      </button>

      <h2>Pessoas</h2>
      <form className="row g-3 mb-4" onSubmit={handleSubmit}>
        <div className="col-md-6">
          <label className="form-label">Email</label>
          <input
            type="email"
            className="form-control"
            value={email}
            onChange={e => setEmail(e.target.value)}
            placeholder="email@exemplo.com"
            required
          />
        </div>
        <div className="col-md-6">
          <label className="form-label">Telefone</label>
          <input
            type="tel"
            className="form-control"
            value={telefone}
            onChange={e => setTelefone(e.target.value)}
            placeholder="(00) 00000-0000"
            required
          />
        </div>
        <div className="col-12 text-end">
          <button className="btn btn-primary">Cadastrar Pessoa</button>
        </div>
      </form>

      <ul className="list-group">
        {pessoas.map(p => (
          <li key={p.id} className="list-group-item">
            <strong>{p.id}</strong> — {p.email} — {p.telefone}
          </li>
        ))}
      </ul>
    </div>
  );
}
