import React, { useState } from 'react';

export default function SortearTimes() {
  const API = process.env.REACT_APP_API_URL;
  const token = localStorage.getItem('accessToken');
  const [teams, setTeams] = useState({ team1: [], team2: [] });
  const [error, setError] = useState('');

  const handleSortear = () => {
    setError('');
    fetch(`${API}/sorteio-times/?qtd=10`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(r => {
        if (!r.ok) throw new Error('Erro ao sortear times');
        return r.json();
      })
      .then(setTeams)
      .catch(e => setError(e.message));
  };

  return (
    <div className="container mt-4">
      <h2>Sorteio de Times</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      <button className="btn btn-primary mb-4" onClick={handleSortear}>
        Sortear 10 Pessoas
      </button>
      <div className="row">
        <div className="col-md-6">
          <h5>Time A</h5>
          <ul className="list-group">
            {teams.team1.map(p => (
              <li key={p.id} className="list-group-item">{p.email}</li>
            ))}
          </ul>
        </div>
        <div className="col-md-6">
          <h5>Time B</h5>
          <ul className="list-group">
            {teams.team2.map(p => (
              <li key={p.id} className="list-group-item">{p.email}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
